#!/usr/bin/env python### https://github.com/abhishekkr/py-gae-legs## phonebook_controller : it's py-gae-legs' default created index file##import refrom ..V.phonebook import phonebook_htmlfrom ..V.phonebook import phonebook_create_htmlfrom ..V.phonebook import phonebook_read_htmlfrom ..V.phonebook import phonebook_update_htmlfrom ..V.phonebook import phonebook_delete_htmldef get_headers(self):  return phonebook_html.get_headers(self)#handling HTTP GET Requestsdef get_(self):  response_ml = """    <html>      <head><title>Not Found</title></head>      <body>        <div style="text-align:center;font-size:24px;">          there have been some error with requested resource        <div>      </body>    </html>"""  requested_res = self.request.path_qs  if '/phonebook'==requested_res:    response_ml = phonebook_html.get_html(self)  elif '/phonebook/V/create'==requested_res:    response_ml = phonebook_create_html.get_html(self)  elif re.compile(r'/phonebook/V/read/.*').match(requested_res):    response_ml = phonebook_read_html.get_html(self)  elif re.compile(r'/phonebook/V/update/.*').match(requested_res):    response_ml = phonebook_update_html.get_html(self)  elif re.compile(r'/phonebook/V/delete/.*').match(requested_res):    response_ml = phonebook_delete_html.get_html(self)  elif '/phonebook/V/read'==requested_res:    response_ml = phonebook_read_html.get_list_html(self)  elif '/phonebook/V/update'==requested_res:    response_ml = phonebook_update_html.get_list_html(self)  elif '/phonebook/V/delete'==requested_res:    response_ml = phonebook_delete_html.get_list_html(self)  else:    response_ml = response_ml    #might wanna log it  return response_ml#handling HTTP POST Requestsdef post_(self):  response_ml = """    <html>      <head><title>Not Found</title></head>      <body>        <div style="text-align:center;font-size:24px;">          there have been some error with requested resource or data posted        <div>      </body>    </html>"""  requested_res = self.request.path_qs  if '/phonebook/M/create'==requested_res:    response_ml = phonebook_create_html.post_html(self)  elif re.compile(r'/phonebook/M/update/.*').match(requested_res):    response_ml = phonebook_update_html.post_html(self)  else:    response_ml = response_ml    #might wanna log it  return response_ml